            a9("CloseConnection           ", sw_52, sw_53, (short)9);

            sw_53.Stop();

            sw_COMPLEATE.Stop();

            SetConsoleTextAttribute(GetStdHandle(-11), (short)75); Console.WriteLine("\n\n\n###### API execution time ######\t\t{0} sec", Math.Round(sw_COMPLEATE.Elapsed.TotalMilliseconds / 10000, 6, MidpointRounding.AwayFromZero));

            Console.ReadLine();

        }

        static void a9(string b, Stopwatch sw_Stop, Stopwatch sw_Start, short color)
        {


            sw_Stop.Stop();

            SetConsoleTextAttribute(GetStdHandle(-11), color);
            //Console.WriteLine(b + "\t\t\t" + sw_Stop.Elapsed.Seconds.ToString() + "\t\t\t" + sw_Stop.Elapsed.Milliseconds.ToString());
            Console.WriteLine(b + "\t\t\t" + Math.Round(sw_Stop.Elapsed.TotalMilliseconds / 10000, 4, MidpointRounding.AwayFromZero).ToString() + " sec");

            sw_Start.Start();
        }

        static byte[] returnDataByte(NavDataSet DataSet)
        {
            var DataSetRef = DataSet.GetType().GetProperty("DataSet", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance | System.Reflection.BindingFlags.Public);
            var recdataset = DataSetRef.GetValue(DataSet);
            byte[] databyte = (byte[])recdataset.GetType().GetProperty("Data").GetValue(recdataset);
            return databyte;
        }

